import logo from './logo.svg';
import './App.css';
import './components/Track/Track'
import Track from './components/Track/Track';
import Tracklist from './components/Tracklist/Tracklist';
import Playlist from './components/Playlist/Playlist';
import SearchBar from './components/SearchBar/SearchBar';
import SearchResults from './components/SearchResults/SearchResults';
import { useEffect, useState } from 'react';
import * as Spotify from './Spotify';
import * as AuthActions from './AuthActions'
import UserProfile from './components/UserProfile/UserProfile';

function App() {
    const [searchResults, setSearchResults] = useState([]);

    const [playlist, setPlaylist] = useState({});

    const [userInfo, setUserInfo] = useState({});

    function checkAuthenticationState(){
        // If there is an auth code in the URL, we need to check the access token and request a new one if it isn't valid
        if(AuthActions.checkAuthCode()){
            console.log("Auth code checked and found");

            if(!AuthActions.checkAccessToken()){
                console.log("Access token not found, requesting a new one");

                AuthActions.getNewAccessToken();

                console.log("Requested a new access token");

                if(AuthActions.checkAccessToken()){
                    console.log("New access token saved");
                } else {
                    console.log("New access token not saved");
                }

            } else {
                AuthActions.testAccessToken()
                .then((tokenValid) => {
                    if(!tokenValid){
                        console.log("Access token not found, requesting a new one");

                        AuthActions.getNewAccessToken();

                        console.log("Requested a new access token");

                        if(AuthActions.checkAccessToken()){
                            console.log("New access token saved");
                        } else {
                            console.log("New access token not saved");
                        }
                    } else {
                        console.log("Access token in storage is valid");
                    }
                })
            }
        // If there is no auth code, check if an access token exists.
        // If it does and it's valid, do nothing
        // If it's not valid, delete it
        } else {
            console.log("Auth code checked and not found");
            if(AuthActions.checkAccessToken()){
                console.log("Found access token, testing...")
                AuthActions.testAccessToken()
                .then((tokenValid) => {
                    if(!tokenValid){
                        console.log("Access token in local storage not valid, deleting...")
                        AuthActions.deleteToken();
                        console.log("Delete access token");
                    } else {
                        console.log("Access token in storage is valid");
                    }
                })
            } else {
                console.log("Access token checked and not found");
            }
        }
    }

    useEffect(() => {
        checkAuthenticationState();
    }, []);

    function addToPlaylistHandler(track){
        if(!playlist.tracks){
            setPlaylist((prev) => {
                return {
                    title: prev.title,
                    id: prev.id,
                    tracks: [
                        track
                    ]
                }
            })
        }
        else if(!playlist.tracks.find((currTrack) => currTrack.id === track.id)){
            setPlaylist((prev) =>{
                return {
                    title: prev.title,
                    tracks: [
                        ...prev.tracks,
                        track
                    ]
                }
            }
        )}
    }

    function removeFromPlaylistHandler(track){
        if(playlist.tracks.find((currTrack) => currTrack.id === track.id)){
            setPlaylist((prev) => {
                return {
                    title: prev.title,
                    tracks: prev.tracks.filter((currTrack) => currTrack.id !== track.id)
                }
            })
        }
    }

    function changePlaylistTitleNameHandler(newTitle){
        setPlaylist((prev) => {
            return {
                title: newTitle,
                tracks: prev.tracks
            }
        })
    }

    async function searchQueryHandler(query){
        if(!query)
            return;

        let results = [];
        results = await Spotify.getSearchResults(query);

        let newResults = [];

        if(results.length != 0){
            newResults = results.map(result => {
                return {
                    title: result.name,
                    artist: result.artists[0].name,
                    album: result.album.name,
                    id: result.id,
                    uri: result.uri
                }
            });
        }

        setSearchResults(newResults);
    }

    async function createPlaylistHandler(){
        if(!playlist || !playlist.tracks || playlist.tracks.length == 0)
            return;

        const {id} = await Spotify.createPlaylist(playlist.title);

        Spotify.addToPlaylist(id, playlist.tracks);

        setPlaylist({});
    }

    return (
        <div className="App">
            { userInfo && <UserProfile user={userInfo} /> }
            <Playlist title={playlist.title} tracks={playlist.tracks} onRemoveTrack={removeFromPlaylistHandler} onTitleChange={changePlaylistTitleNameHandler} onCreate={createPlaylistHandler} />
            <SearchBar onSearchQuery={searchQueryHandler} />
            <SearchResults searchResults={searchResults} onAddTrack={addToPlaylistHandler} />
        </div>
    );
}

export default App;
