import axios from "axios";

axios.defaults.baseURL = "https://accounts.spotify.com/";

async function requestAccessToken(){
    const clientID = "a23843d079a34f28a8368672dc8a7bcd";
    const clientSecret = "d98f121afd3b4d9babc64ce90f4d35f2";

    const token = fetch("https://accounts.spotify.com/api/token", {
        method: "POST",
        headers: {
            "Content-Type": "application/x-www-form-urlencoded"
        },
        body: JSON.stringify({
            grant_type: "client_credentials",
            client_id: clientID,
            client_secret: clientSecret
        })
    });

    console.log(token);
}

export {requestAccessToken};
