import axios from "axios";

let apiToken = "";
let baseApiUrl = "https://api.spotify.com";
const clientId = "a23843d079a34f28a8368672dc8a7bcd";
const clientSecret = "d98f121afd3b4d9babc64ce90f4d35f2";

async function requestAccessToken(){
    const tokenUrl = "https://accounts.spotify.com/api/token";

    let response = await fetch(tokenUrl, {
        body: `grant_type=client_credentials&client_id=${clientId}&client_secret=${clientSecret}`,
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"
        },
        method: "POST"
    });
    response = await response.json();
    let token = response.access_token;

    apiToken = token;
}

async function getArtist(artistID){
    const baseUrl = baseApiUrl + "/v1/artists";

    let response = await fetch(`${baseUrl}/${artistID}`, {
        headers: {
            Authorization: `Bearer  ${apiToken}`
        }
    });
    response = await response.json();
    console.log(response);
}

async function getSearchResults(searchQuery){
    const baseUrl = baseApiUrl + "/v1/search";

    let queryString = new URLSearchParams({
        q: searchQuery,
        type: [
            "track"
        ]
    })

    if (!apiToken){
        await requestAccessToken();
    }

    let response = await fetch(`${baseUrl}?${queryString.toString()}`, {
        headers: {
          Authorization: `Bearer  ${apiToken}`
        }
    });
    let data = await response.json();

    return data.tracks.items;
}

function generateRandomString(length) {
    let text = '';
    let possible = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';

    for (let i = 0; i < length; i++) {
      text += possible.charAt(Math.floor(Math.random() * possible.length));
    }
    return text;
}

async function generateCodeChallenge(codeVerifier) {
    function base64encode(string) {
      return btoa(String.fromCharCode.apply(null, new Uint8Array(string)))
        .replace(/\+/g, '-')
        .replace(/\//g, '_')
        .replace(/=+$/, '');
    }

    const encoder = new TextEncoder();
    const data = encoder.encode(codeVerifier);
    const digest = await window.crypto.subtle.digest('SHA-256', data);

    return base64encode(digest);
}

async function authorize(){
    const redirectUri = 'http://localhost:3000';

    let codeVerifier = generateRandomString(128);

    generateCodeChallenge(codeVerifier).then(codeChallenge => {
        let state = generateRandomString(16);
        let scope = 'user-read-private user-read-email';

        localStorage.setItem('code_verifier', codeVerifier);

        let args = new URLSearchParams({
            response_type: 'code',
            client_id: clientId,
            scope: scope,
            redirect_uri: redirectUri,
            state: state,
            code_challenge_method: 'S256',
            code_challenge: codeChallenge
        });

        window.location = 'https://accounts.spotify.com/authorize?' + args;
    });

    const urlParams = new URLSearchParams(window.location.search);
    let code = urlParams.get('code');
}


export {requestAccessToken, getSearchResults, getArtist, authorize};
