import logo from './logo.svg';
import './App.css';
import './components/Track/Track'
import Track from './components/Track/Track';
import Tracklist from './components/Tracklist/Tracklist';
import Playlist from './components/Playlist/Playlist';
import SearchBar from './components/SearchBar/SearchBar';
import SearchResults from './components/SearchResults/SearchResults';
import { useState } from 'react';
import { requestAccessToken, getSearchResults } from './Spotify';

function App() {
  const [searchResults, setSearchResults] = useState([
    {
        title: "Test search result 6",
        artist: "Test artist 6",
        album: "Test album 6",
        id: "6"
    },
    {
        title: "Test search result 7",
        artist: "Test artist 7",
        album: "Test album 7",
        id: "7"
    },
    {
        title: "Test search result 8",
        artist: "Test artist 8",
        album: "Test album 8",
        id: "8"
    }
]);

  const [playlist, setPlaylist] = useState({
    title: "Sample playlist",
    tracks: [
      {
          title: "Test title 1",
          artist: "Test artist 1",
          album: "Test album 1",
          id: "1"
      },
      {
          title: "Test title 2",
          artist: "Test artist 2",
          album: "Test album 2",
          id: "2"
      },
      {
          title: "Test title 3",
          artist: "Test artist 3",
          album: "Test album 3",
          id: "3"
      },
      {
          title: "Test title 4",
          artist: "Test artist 4",
          album: "Test album 4",
          id: "4"
      },
      {
          title: "Test title 5",
          artist: "Test artist 5",
          album: "Test album 5",
          id: "5"
      }
    ]
  });

  function addToPlaylistHandler(track){
    if(!playlist.tracks.find((currTrack) => currTrack.id === track.id)){
      setPlaylist((prev) =>{
        return {
          title: prev.title,
          tracks: [
            ...prev.tracks,
            track
          ]
        }
      }
    )}
  }

  function removeFromPlaylistHandler(track){
    if(playlist.tracks.find((currTrack) => currTrack.id === track.id)){
      setPlaylist((prev) => {
        return {
          title: prev.title,
          tracks: prev.tracks.filter((currTrack) => currTrack.id !== track.id)
        }
      })
    }
  }

  function changePlaylistTitleNameHandler(newTitle){
    setPlaylist((prev) => {
      return {
        title: newTitle,
        tracks: prev.tracks
      }
    })
  }

  async function searchQueryHandler(query){
    let results = await getSearchResults(query);

    console.log(results);

    setSearchResults(results.map(result => {
      return {
        title: result.name,
        artist: result.artists[0],
        album: result.album.name,
        id: result.id
      }
    }));

  }

  return (
    <div className="App">
      <Playlist title={playlist.title} tracks={playlist.tracks} onRemoveTrack={removeFromPlaylistHandler} onTitleChange={changePlaylistTitleNameHandler} />
      <SearchBar onSearchQuery={searchQueryHandler} />
      <SearchResults searchResults={searchResults} onAddTrack={addToPlaylistHandler} />
    </div>
  );
}

export default App;
